.hero-header__section {
  background-color: var(--hero-bg);
  height: 502px;
  @media screen and (min-width: 768px) {
    height: 400px;
  }
  @media screen and (min-width: 1280px) {
    height: 665px;
  }
}

.hero-header__container {
  position: relative;
  height: 100%;
  overflow: hidden;

  &::after {
    position: absolute;
    width: 346px;
    height: 346px;
    content: '';
    top: 83px;
    left: calc(50% - 124px);

    border-radius: 50%;
    background-color: #ffa5ba;
    z-index: 1;

    @media screen and (min-width: 768px) {
      top: 31px;
      left: calc(50% - 77px);
      width: 324px;
      height: 324px;
    }
    @media screen and (min-width: 1280px) {
      top: 52px;
      left: calc(50% - 160px);
      width: 538px;
      height: 536px;
    }
  }
}

.ice__img {
  position: absolute;

  width: 171px;
  height: 410px;
  top: 92px;
  left: calc(50% - 42px);
  background-image: url('../images/hero/ice-m@1x.png');
  background-repeat: no-repeat;
  background-size: contain;
  z-index: 2;
  opacity: 0;
  /*добавить анимацию*/

  @media screen and (min-width: 768px) {
    top: 63px;
    left: calc(50% - 6px);
    width: 143px;
    height: 336px;
    background-size: contain;
    background-image: url('../images/hero/ice-t@1x.png');
  }
  @media screen and (min-width: 1280px) {
    top: 108px;
    left: calc(50% - 40px);
    width: 236px;
    height: 557px;
    background-size: contain;
    background-image: url('../images/hero/ice@1x.png');
  }
  @media screen and (min-device-pixel-ratio: 2),
    screen and (-webkit-min-device-pixel-ratio: 2),
    screen and (min-resolution: 192dpi),
    screen and (min-resolution: 2dppx) {
    background-image: url('../images/hero/ice-m@2x.png');
    @media screen and (min-width: 768px) {
      background-image: url('../images/hero/ice-t@2x.png');
    }
    @media screen and (min-width: 1280px) {
      background-image: url('../images/hero/ice@2x.png');
    }
  }
}

.girl__img {
  opacity: 0;
  /*анимация*/

  @media screen and (min-width: 768px) {
    position: absolute;
    bottom: 0;
    right: 34px;
    width: 168px;
    height: 133px;
    background-repeat: no-repeat;
    background-size: contain;
    background-image: url('../images/hero/girl-t@1x.png');
    z-index: 21;
  }
  @media screen and (min-width: 1280px) {
    position: absolute;
    bottom: 0;
    right: 110px;
    width: 293px;
    height: 232px;
    background-repeat: no-repeat;
    background-image: url('../images/hero/girl@1x.png');
    background-size: contain;
  }
  @media screen and (min-device-pixel-ratio: 2),
    screen and (-webkit-min-device-pixel-ratio: 2),
    screen and (min-resolution: 192dpi),
    screen and (min-resolution: 2dppx) {
    @media screen and (min-width: 768px) {
      background-image: url('../images/hero/girl-t@2x.png');
    }
    @media screen and (min-width: 1280px) {
      background-image: url('../images/hero/girl@2x.png');
    }
