.menu__button {
  position: fixed;
  top: 20px;
  right: 28px;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 32px;
  height: 32px;
  border-radius: 5px;
  margin: 0;
  padding: 0;
  border: none;
  background-color: rgba(0, 0, 0, 0.03);
  z-index: 30;
  @media screen and (min-width: 768px) {
    position: absolute;
    top: 47px;
    right: 190px;
  }
  @media screen and (min-width: 1280px) {
    display: none;
  }
  &.is-open {
    @media screen and (min-width: 768px) {
      position: fixed;
      top: 20px;
      right: 20px;
    }
  }
}
.menu__button .close__icon {
  display: none;
}
.menu__button.is-open + .order-t__button {
  @media screen and (min-width: 768px) and (max-width: 1280px) {
    display: none;
  }
}
.menu__button.is-open .close__icon {
  display: block;
}

.menu__button.is-open .menu__icon {
  display: none;
}

.btn__container {
  position: fixed;
  top: 0;
  right: 0;
  width: 100%;
  min-height: 100vh;
  height: 100%;
  background-color: blue;
  z-index: 29;
  display: none;

  @media screen and (min-width: 768px) {
    width: 254px;
  }
  /* @media screen and (max-width: 1280px) {

  }  добавить анимацию*/
  @media screen and (min-width: 1280px) {
    position: relative;
    display: block;
    width: 600px;
    margin: 0 auto;
    min-height: 70px;
    background-color: transparent;
  }
  &.is-open {
    display: block;
    overflow: visible;
  }
}

.order__button {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 60px auto 0 auto;
  width: 210px;
  height: 44px;
  border-radius: 22px;
  border: none;
  background-color: #ffffff;
  transition: background-color var(--duration) var(--timing-function);

  @media screen and (min-width: 768px) {
    width: 186px;
  }
  @media screen and (min-width: 1280px) {
    display: none;
  }

  &:hover,
  &:focus {
    background-color: #ecebeb;
  }
}

.order-t__button {
  display: none;
  @media screen and (min-width: 768px) {
    position: absolute;
    display: flex;
    justify-content: center;
    align-items: center;
    top: 38px;
    right: 0;
    width: 160px;
    height: 44px;
    border-radius: 22px;
    border: none;
    background-color: #ffffff;
    z-index: 13;
    transition: background-color var(--duration) var(--timing-function);
    &:hover,
    &:focus {
      background-color: #ecebeb;
    }
  }
}

.order__text {
  display: block;
  padding-right: 25px;
  color: var(--focus-color);
  font-family: var(--hero-font);
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 1.31;
}
.order__img {
  background: transparent;
}

/*NAV*/
.nav__list {
  margin-top: 20px;
  @media screen and (min-width: 1280px) {
    display: flex;
    justify-content: end;
    margin-top: 0;
    padding-top: 38px;
  }
}
.nav__item {
  padding-left: 21px;
  padding-top: 12px;
  padding-bottom: 12px;
  border-top: 1px solid #e18298;
  border-bottom: 1px solid #e18298;

  @media screen and (min-width: 1280px) {
    padding-left: 0;
    border: none;
    &:not(:last-child) {
      margin-right: 35px;
    }
  }
}
.nav__link {
  color: #ffffff;
  font-family: var(--hero-font);
  font-style: normal;
  font-weight: 700;
  font-size: 14px;
  line-height: 1.29;
  transition: color var(--duration) var(--timing-function);

  @media screen and (min-width: 1280px) {
    font-size: 16px;
    line-height: 1.31;
  }
}

.nav__link:hover,
.nav__link:focus {
  color: var(--focus-color);
}

.nav__link--current {
  color: var(--focus-color);
}

/*HERO*/

.hero {
  position: absolute;
  top: 193px;
  z-index: 20;

  @media screen and (min-width: 768px) {
    top: 102px;
  }
  @media screen and (min-width: 1280px) {
    top: 171px;
    left: 115px;
  }
  &__title {
    margin: 0;
    font-family: var(--hero-font);
    font-style: normal;
    font-weight: normal;
    text-transform: uppercase;
  }
  &__title--part {
    display: block;
    color: #ffffff;
    font-size: 26px;
    line-height: 1.15;
    letter-spacing: 0.04em;
    @media screen and (min-width: 768px) {
      font-size: 22px;
      line-height: 1.14;
    }
    @media screen and (min-width: 1280px) {
      font-size: 38px;
      line-height: 1.16;
    }
  }
  &__title--accent {
    display: block;
    color: var(--focus-color);
    font-size: 48px;
    line-height: 0.97;
    letter-spacing: 0.04em;

    @media screen and (min-width: 768px) {
      font-size: 34px;
      line-height: 0.91;
    }
    @media screen and (min-width: 1280px) {
      font-size: 52px;
      line-height: 0.91;
    }
  }
}

.product__button {
  display: inline-block;
  margin-top: 20px;
  padding-top: 8px;
  text-align: center;
  width: 62px;
  height: 24px;
  border-radius: 12px;
  color: #ffffff;
  font-family: var(--hero-font);
  font-style: normal;
  font-weight: normal;
  font-size: 8px;
  line-height: 1.25;
  background-color: var(--focus-color);
  transition: background-color var(--duration) var(--timing-function);

  @media screen and (min-width: 1280px) {
    margin-top: 25px;
    padding-top: 11px;
    width: 104px;
    height: 40px;
    border-radius: 20px;
    font-size: 14px;
    line-height: 1.29;
  }
}
.product__button:hover,
.product__button:focus {
  background-color: #c7133e;
}

.made__button {
  display: inline-block;
  margin-left: 8px;
  margin-top: 20px;
  padding-top: 8px;
  text-align: center;
  width: 83px;
  height: 24px;
  border-radius: 12px;
  color: var(--focus-color);
  font-family: var(--hero-font);
  font-style: normal;
  font-weight: normal;
  font-size: 8px;
  line-height: 1.25;
  background-color: #ffffff;
  transition: background-color var(--duration) var(--timing-function);

  @media screen and (min-width: 1280px) {
    margin-top: 25px;
    margin-left: 15px;
    padding-top: 11px;
    width: 138px;
    height: 40px;
    border-radius: 20px;
    font-size: 14px;
    line-height: 1.29;
  }
}
.made__button:hover,
.made__button:focus {
  background-color: #ecebeb;
}

/*HERO  milk*/
.hero__bottom {
  display: none;
  @media screen and (min-width: 768px) {
    display: block;
  }
}

.hero__content {
  position: absolute;

  top: 292px;
  left: 158px;
  width: 114px;

  @media screen and (min-width: 1280px) {
    top: 485px;
    left: 295px;
    width: 200px;
  }
}

.hero__item {
  color: #ffffff;
  font-size: 8px;
  line-height: 1.5;

  @media screen and (min-width: 1280px) {
    font-size: 14px;
    line-height: 1.4;
  }

  &::before {
    position: absolute;
    content: '';
    bottom: -6px;
    left: -124px;
    width: 124px;
    height: 126px;
    background-repeat: no-repeat;
    background-size: contain;
    background-image: url('../images/hero/milk-t@1x.png');
    z-index: 10;
    @media screen and (min-width: 1280px) {
      top: -56px;
      left: -218px;
      width: 188px;
      height: 191px;
      background-image: url('../images/hero/milk@1x.png');
    }
    @media screen and (min-device-pixel-ratio: 2),
      screen and (-webkit-min-device-pixel-ratio: 2),
      screen and (min-resolution: 192dpi),
      screen and (min-resolution: 2dppx) {
      background-image: url('../images/hero/milk-t@2x.png');

      @media screen and (min-width: 1280px) {
        background-image: url('../images/hero/milk@2x.png');
      }
    }
  }
}

.hero__link {
  display: block;
  width: 18px;
  height: 18px;
  border-radius: 50%;
  background-color: #ffffff;
  margin-top: 7px;
  fill: var(--focus-color);

  justify-content: center;
  align-items: center;
  @media screen and (min-width: 1280px) {
    width: 26px;
    height: 26px;
    margin-top: 20px;
  }
}

/*number*/

.number__list {
  position: absolute;

  top: 94px;
  right: 44px;

  @media screen and (min-width: 1280px) {
    top: 156px;
    right: 115px;
  }
}
.number__link {
  position: relative;
  text-align: right;
  z-index: 20;
}
.number__link:not(:last-child) {
  padding-bottom: 13px;
  @media screen and (min-width: 1280px) {
    padding-bottom: 15px;
  }
}

.number__link::after {
  position: absolute;
  content: '';

  z-index: -1;

  top: -2px;
  right: -10px;
  width: 16px;
  height: 16px;
  border-radius: 50%;
  background-color: #ffffff;

  @media screen and (min-width: 1280px) {
    top: -4px;
    right: -18px;
    width: 30px;
    height: 30px;
  }
}

.number__item {
  color: var(--focus-color);
  font-family: var(--hero-font);
  font-weight: normal;
  font-size: 28px;
  line-height: 1.14;
  letter-spacing: 0.04em;
  text-transform: uppercase;

  @media screen and (min-width: 1280px) {
    font-size: 46px;
    line-height: 1.15;
  }
}

.number__text {
  color: #ffffff;
  font-family: var(--hero-font);
  font-weight: normal;
  font-size: 8px;
  line-height: 1.25;
  @media screen and (min-width: 1280px) {
    font-size: 14px;
    line-height: 1.29;
  }
}

